    fun compliantFromSonar() {
        Pattern.compile("(\\w+)\\s*([<>]?[=]?)\\s*(.*)", Pattern.CASE_INSENSITIVE)
        Regex("(\\w+)\\s*([<>]?[=]?)\\s*(.*)", RegexOption.IGNORE_CASE)
    }

    fun compliantFromGuava(): Regex {
        val decimal = "(?:\\d++(?:\\.\\d*+)?|\\.\\d++)"
        val completeDec = "$decimal(?:[eE][+-]?\\d++)?[fFdD]?"
        val hex = "(?:\\p{XDigit}++(?:\\.\\p{XDigit}*+)?|\\.\\p{XDigit}++)"
        val completeHex = "0[xX]$hex[pP][+-]?\\d++[fFdD]?"
        val fpPattern = "[+-]?(?:NaN|Infinity|$completeDec|$completeHex)"
        return Regex(fpPattern)
    }

    fun guavaNonCompliantVersion(): Regex {
        // Noncompliant@+2 {{Simplify this regular expression to reduce its complexity from 58 to the 20 allowed.}}
        val fpPattern =
            "[+-]?(?:NaN|Infinity|(?:\\d++(?:\\.\\d*+)?|\\.\\d++)(?:[eE][+-]?\\d++)?[fFdD]?|" +
                "0[xX](?:\\p{XDigit}++(?:\\.\\p{XDigit}*+)?|\\.\\p{XDigit}++)[pP][+-]?\\d++[fFdD]?)"
        return Regex(fpPattern)
    }
